import axios from "axios";
import React, { useEffect, useState } from "react";
import { Container, Row, Button, Col, Table, Modal } from "react-bootstrap";
import { Link } from "react-router-dom";
import { URLS } from "../../routes";
import displayToast from "../../utils/displayToast";
import "./ManageProduct.css";
function ManageProduct() {
  const [products, setProducts] = useState([]);
  const [currentProduct, setCurrentProduct] = useState(null);

  const [show, setShow] = useState(false);
  const handleClose = () => {
    setShow(false);
    setCurrentProduct(null);
  };

  const handleShow = () => setShow(true);

  useEffect(() => {
    let isActive = true;

    if (isActive) {
      fetchProducts();
    }
    return () => {
      isActive = false;
    };
  }, []);

  const fetchProducts = async () => {
    const url = URLS.GET_ALL_PRODUCTS;
    axios
      .get(url)
      .then(function (response) {
        // console.log(response);
        setProducts(response.data);
      })
      .catch(function (error) {
        console.log(error);
        displayToast({ type: "error", msg: "Oops! Something went wrong" });
      });
  };

  const deleteProductConfirmation = (b) => {
    setCurrentProduct(b);
    handleShow();
  };

  const deleteBuyer = async () => {
    const url = URLS.DELETE_PRODUCT + currentProduct.id;
    // const data = {
    //     id : currentBuyer.id
    // };
    axios
      .delete(url)
      .then(function (response) {
        handleClose();
        // console.log(response);
        displayToast({ type: "success", msg: "Product deleted successfully!" });
        fetchProducts();
      })
      .catch(function (error) {
        console.log(error);
        displayToast({ type: "error", msg: "Oops! Something went wrong" });
      });
  };

  return (
    <Container className="container-main">
    <Row className="header-row">
      <Col md={6}>
        <h3>Product List</h3>
      </Col>
      <Col md={6} className="add-product-col">
        <Link to="/add-product">
          <Button variant="primary" className="add-product-button">
            Add New Product
          </Button>
        </Link>
        </Col>

      </Row>
      <Row>
        <Table striped bordered hover>
          <div id="customers">
            <thead>
              <tr>
                <th>Sr. No.</th>
                <th>Product Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Action</th>
              </tr>
            </thead>

            <tbody>
              {products.map((product, index) => {
                const { id, productName, quantity, price = 0 } = product;

                return (
                  <tr key={id}>
                    <td>{index + 1}</td>
                    <td>{productName}</td>
                    <td>{price}</td>
                    <td>{quantity}</td>
                    <td>
                      <Link to={`/edit-product/?id=${id}`}>
                        <Button variant="secondary">Edit</Button>{" "}
                      </Link>
                      <Button
                        onClick={() => deleteProductConfirmation(product)}
                        variant="danger"
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </div>
        </Table>
      </Row>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Delete Confirmation</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete{" "}
          {currentProduct ? currentProduct.productName : ""}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="danger" onClick={deleteBuyer}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default ManageProduct;
